void main()
{
  vec2 p = gl_FragCoord.xy / iResolution;
  vec2 offset = vec2(iOffset.x + 0.5, 0.5 - iOffset.y);

  const int samples = 10;
  float blurStart = 1.0;
  float blurWidth = 0.1;

  p -= offset;
  float precompute = blurWidth * (1.0 / float(iSamples - 1));

  vec4 color = vec4(0.0);
  for(int i = 0; i < samples; i++)
  {
    if (i > iSamples) {
      break;
    }

    float scale = blurStart + (float(i)* precompute);
    color += texture2D(iTexture, p * scale + offset);
  }


  color /= float(iSamples);
  gl_FragColor = color;
}
