fragmentShader:
void main()
 {
 vec2 q = gl_FragCoord.xy / iResolution.xy;
 vec3 bg = texture2D( iTexture2, q ).xyz;
 vec3 fg = texture2D( iTexture, q ).xyz;
 float maxrb = max( fg.r, fg.b );
 float k = clamp( (fg.g-maxrb)*5.0, 0.0, 1.0 );
 float ll = length( fg );
 fg.g = min( fg.g, maxrb*0.8 );
 fg = ll*normalize(fg);
 gl_FragColor = vec4( mix(fg, bg, k), 1.0 );
 }
